mapper.select:
select * from product a left outer join order_detail b
on a.product_id = b.product_id left outer join cart c 
on b.order_detail_pk = c.order_detail_pk where c.member_id = #{member_id}


	select * from 
	 (select rownum rnum, member_id, review_id, product_id, review_img, review_subject, review_content, review_date
	 from (select * from review order by review_id desc)) 
   		 where rnum >= ((#{page}-1) * 5+1)  and rnum <= (#{page} * 5) and member_id = #{member_id}


	select * from 
	 (select rownum rnum, product_id, question_id, question_content, member_id, question_subject, question_date, question_state, question_type
	 from (select * from question order by question_id desc)) 
   			where rnum >= ((#{page}-1) * 5+1)  and rnum <= (#{page} * 5) and member_id = #{member_id}


	select * from 
	 (select rownum rnum, member_id, question_id, question_content, question_subject, question_date, question_state, question_type
	 from (select * from personal_question order by question_id desc)) 
   		 where rnum >= ((#{page}-1) * 5+1)  and rnum <= (#{page} * 5) and member_id = #{member_id}
